mapper.select:
select title as courseTitle, introduction, upload_date, user_seq, thumbnail_path, thumbnail_name, category, course_seq, difficulty, is_public 
 from course 
 where user_seq = #{userSeq}

 select course_seq, title as courseTitle, introduction,
upload_date, user_seq, thumbnail_path, thumbnail_name,
category, difficulty, is_public, video_count, quiz_count,  (video_count + quiz_count) AS content_count from course where course_seq = #{courseSeq}
